import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import formData from "../JSON/formData.json";

const OnePagePerSection = () => {
    const { formId } = useParams();
    const form = formData[formId];
    const questions = form?.fields;
    const sections = [...new Set(questions.map(q => q.sectionId))];
    const [answers, setAnswers] = useState({});
    const [errors, setErrors] = useState({});

    const handleChange = (fieldId, value) => {
        setAnswers({
            ...answers,
            [fieldId]: value,
        });
    };

    const validateInput = () => {
        const newErrors = {};

        questions.forEach((question) => {
            const value = answers[question.fieldId] || '';

            // Check if the field is required
            if (question.required && !value) {
                if (question.type === 'checkbox' && (!Array.isArray(value) || value.length === 0)) {
                    newErrors[question.fieldId] = 'Please select at least one option.';
                } else if (question.type === 'textarea' && !value.trim()) {
                    newErrors[question.fieldId] = 'This field cannot be empty.';
                } else if (question.type !== 'checkbox' && question.type !== 'textarea') {
                    newErrors[question.fieldId] = 'This field is required.';
                }
            }

            // Validate based on the question type
            if (question.type === 'textbox' && value && !/^[A-Za-z\s]+$/.test(value)) {
                newErrors[question.fieldId] = 'Only letters and spaces are allowed.';
            } else if (question.type === 'email' && value && !/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(value)) {
                newErrors[question.fieldId] = 'Please enter a valid email address.';
            }
        });

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = () => {
        if (validateInput()) {
            console.log('Selected Answers:', answers);
        }
    };

    return (
        <div className="container">
            {sections.map(sectionId => (
                <div key={sectionId} className="mb-4">
                    <h2 className='text-center'>Section {sectionId}</h2>
                    <div className="p-3 mt-3">
                        <div className="row justify-content-center">
                            {questions.filter(q => q.sectionId === sectionId).map((question) => (
                                <div key={question.fieldId} className="col-xl-4 col-lg-4 col-md-6 col-sm-12 mb-3">
                                    <div className="card p-3 border">
                                        <label>{question.label}</label>
                                        {question.type === 'textbox' && (
                                            <input
                                                type="text"
                                                className="form-control"
                                                placeholder={question.placeholder}
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            />
                                        )}
                                        {question.type === 'email' && (
                                            <input
                                                type="email"
                                                className="form-control"
                                                placeholder={question.placeholder}
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            />
                                        )}
                                        {question.type === 'date' && (
                                            <input
                                                type="date"
                                                className="form-control"
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            />
                                        )}
                                        {question.type === 'radio' && question.options.map(option => (
                                            <div key={option.id}>
                                                <input
                                                    type="radio"
                                                    name={question.fieldId}
                                                    value={option.label}
                                                    onChange={() => handleChange(question.fieldId, option.label)}
                                                />
                                                <label>{option.label}</label>
                                            </div>
                                        ))}
                                        {question.type === 'checkbox' && question.options.map(option => (
                                            <div key={option.id}>
                                                <input
                                                    type="checkbox"
                                                    name={question.fieldId}
                                                    value={option.label}
                                                    onChange={(e) => {
                                                        const currentAnswers = answers[question.fieldId] || [];
                                                        if (e.target.checked) {
                                                            handleChange(question.fieldId, [...currentAnswers, option.label]);
                                                        } else {
                                                            handleChange(question.fieldId, currentAnswers.filter(item => item !== option.label));
                                                        }
                                                    }}
                                                />
                                                <label>{option.label}</label>
                                            </div>
                                        ))}
                                        {question.type === 'selectbox' && (
                                            <select
                                                className="form-control"
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            >
                                                <option value="">{question.placeholder}</option>
                                                {question.options.map(option => (
                                                    <option key={option.id} value={option.label}>{option.label}</option>
                                                ))}
                                            </select>
                                        )}
                                        {question.type === 'textarea' && (
                                            <textarea
                                                className="form-control"
                                                placeholder={question.placeholder}
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            />
                                        )}
                                        {errors[question.fieldId] && (
                                            <div className="text-danger mt-2">{errors[question.fieldId]}</div>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
            <div className="text-center mt-4">
                <button className="btn btn-primary" onClick={handleSubmit}>Submit</button>
            </div>
        </div>
    );
};

export default OnePagePerSection;
