import React, { useState } from 'react';
import formData from "../JSON/formData.json";
import { useParams } from 'react-router-dom';

const AllQuestions = () => {
    const { formId } = useParams();
    const form = formData[formId];
    const questions = form?.fields;
    const [answers, setAnswers] = useState({});
    const [errors, setErrors] = useState({});

    const handleChange = (fieldId, value) => {
        setAnswers({
            ...answers,
            [fieldId]: value,
        });
    };

    const validateInput = () => {
        const newErrors = {};
        questions.forEach((question) => {
            const value = answers[question.fieldId] || '';

            // Validate based on the question type
            if (!value) {
                newErrors[question.fieldId] = 'This field is required.';
            } else if (question.type === 'textbox' && !/^[A-Za-z\s]+$/.test(value)) {
                newErrors[question.fieldId] = 'Only letters and spaces are allowed.';
            } else if (question.type === 'email' && !/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(value)) {
                newErrors[question.fieldId] = 'Please enter a valid email address.';
            }
        });

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (validateInput()) {
            console.log('Selected Answers:', answers);
        }
    };

    return (
        <div className="container my-4">
            <h2 className="text-center mb-4">All Questions</h2>
            <form onSubmit={handleSubmit} className="row">
                {questions.map((question) => (
                    <div key={question.fieldId} className="col-12 col-md-6 mb-4">
                        <div className="card shadow-sm p-3 h-100">
                            <label className="form-label fw-bold">{question.label}</label>
                            {question.type === 'textbox' && (
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder={question.placeholder}
                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                    required={question.required}
                                />
                            )}
                            {question.type === 'email' && (
                                <input
                                    type="email"
                                    className="form-control"
                                    placeholder={question.placeholder}
                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                    required={question.required}
                                />
                            )}
                            {question.type === 'date' && (
                                <input
                                    type="date"
                                    className="form-control"
                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                    required={question.required}
                                />
                            )}
                            {question.type === 'radio' && (
                                <div>
                                    {question.options.map((option) => (
                                        <div key={option.id} className="form-check">
                                            <input
                                                className="form-check-input"
                                                type="radio"
                                                name={question.fieldId}
                                                value={option.label}
                                                onChange={() => handleChange(question.fieldId, option.label)}
                                                required={question.required}
                                            />
                                            <label className="form-check-label">{option.label}</label>
                                        </div>
                                    ))}
                                </div>
                            )}
                            {question.type === 'checkbox' && (
                                <div>
                                    {question.options.map((option) => (
                                        <div key={option.id} className="form-check">
                                            <input
                                                className="form-check-input"
                                                type="checkbox"
                                                name={question.fieldId}
                                                value={option.label}
                                                required={question.required}
                                                onChange={(e) => {
                                                    const currentAnswers = answers[question.fieldId] || [];
                                                    if (e.target.checked) {
                                                        handleChange(question.fieldId, [...currentAnswers, option.label]);
                                                    } else {
                                                        handleChange(
                                                            question.fieldId,
                                                            currentAnswers.filter((item) => item !== option.label)
                                                        );
                                                    }
                                                }}
                                            />
                                            <label className="form-check-label">{option.label}</label>
                                        </div>
                                    ))}
                                </div>
                            )}
                            {question.type === 'selectbox' && (
                                <select
                                    className="form-select"
                                    required={question.required}
                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                >
                                    <option value="">{question.placeholder}</option>
                                    {question.options.map((option) => (
                                        <option key={option.id} value={option.label}>
                                            {option.label}
                                        </option>
                                    ))}
                                </select>
                            )}
                            {question.type === 'textarea' && (
                                <textarea
                                    className="form-control"
                                    required={question.required}
                                    placeholder={question.placeholder}
                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                />
                            )}
                            {errors[question.fieldId] && (
                                <div className="text-danger mt-2">{errors[question.fieldId]}</div>
                            )}
                        </div>
                    </div>
                ))}
                <div className="col-12 text-center mt-4">
                    <button type="submit" className="btn btn-primary px-5">
                        Submit
                    </button>
                </div>
            </form>
        </div>
    );
};

export default AllQuestions;
