import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import formData from "../JSON/formData.json";

const OnePagePerSection = () => {
    const { formId } = useParams();
    const form = formData[formId];
    const questions = form?.fields;
    const sections = [...new Set(questions.map(q => q.sectionId))];
    const [answers, setAnswers] = useState({});
    const [errors, setErrors] = useState({}); // State to track errors

    const handleChange = (fieldId, value) => {
        setAnswers({
            ...answers,
            [fieldId]: value,
        });
        setErrors({ ...errors, [fieldId]: '' }); // Clear error when user starts typing
    };

    const handleSubmit = () => {
        let isValid = true;
        let errorMessages = {};

        // Regex for email validation
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        // Check for required fields
        questions.forEach(question => {
            if (question.required && !answers[question.fieldId]) {
                isValid = false;
                errorMessages[question.fieldId] = 'This field is required'; // Set error message for required field
            }
        });

        // Email validation
        const emailField = questions.find(q => q.type === 'email');
        if (emailField && !answers[emailField.fieldId]) {
            isValid = false;
            errorMessages[emailField.fieldId] = 'Valid email is required';
        } else if (emailField && answers[emailField.fieldId] && !emailRegex.test(answers[emailField.fieldId])) {
            isValid = false;
            errorMessages[emailField.fieldId] = 'Please enter a valid email address';
        }

        // Radio button validation
        const radioFields = questions.filter(q => q.type === 'radio');
        radioFields.forEach((radioField) => {
            if (!answers[radioField.fieldId]) {
                isValid = false;
                errorMessages[radioField.fieldId] = 'Selection is required';
            }
        });

        // Set errors if any
        setErrors(errorMessages);

        if (isValid) {
            console.log('Selected Answers:', answers);
        } else {
            console.log('Form has errors:', errorMessages);
        }
    };

    return (
        <div className="container">
            {sections.map(sectionId => (
                <div key={sectionId} className="mb-4">
                    <h2 className='text-center'>Section {sectionId}</h2>
                    <div className="p-3 mt-3">
                        <div className="row justify-content-center">
                            {questions.filter(q => q.sectionId === sectionId).map((question) => (
                                <div key={question.fieldId} className="col-xl-4 col-lg-4 col-md-6 col-sm-12 mb-3">
                                    <div className="card p-3 border">
                                        <label>{question.label}</label>
                                        {question.type === 'textbox' && (
                                            <div>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    placeholder={question.placeholder}
                                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                                />
                                                {errors[question.fieldId] && (
                                                    <small className="text-danger">{errors[question.fieldId]}</small>
                                                )}
                                            </div>
                                        )}
                                        {question.type === 'email' && (
                                            <div>
                                                <input
                                                    type="email"
                                                    className="form-control"
                                                    placeholder={question.placeholder}
                                                    onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                                />
                                                {errors[question.fieldId] && (
                                                    <small className="text-danger">{errors[question.fieldId]}</small>
                                                )}
                                            </div>
                                        )}
                                        {question.type === 'date' && (
                                            <input
                                                type="date"
                                                className="form-control"
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            />
                                        )}
                                        {question.type === 'radio' && question.options.map(option => (
                                            <div key={option.id}>
                                                <input
                                                    type="radio"
                                                    name={question.fieldId}
                                                    value={option.label}
                                                    onChange={() => handleChange(question.fieldId, option.label)}
                                                />
                                                <label>{option.label}</label>
                                            </div>
                                        ))}
                                        {question.type === 'checkbox' && question.options.map(option => (
                                            <div key={option.id}>
                                                <input
                                                    type="checkbox"
                                                    name={question.fieldId}
                                                    value={option.label}
                                                    onChange={(e) => {
                                                        const currentAnswers = answers[question.fieldId] || [];
                                                        if (e.target.checked) {
                                                            handleChange(question.fieldId, [...currentAnswers, option.label]);
                                                        } else {
                                                            handleChange(question.fieldId, currentAnswers.filter(item => item !== option.label));
                                                        }
                                                    }}
                                                />
                                                <label>{option.label}</label>
                                            </div>
                                        ))}
                                        {question.type === 'selectbox' && (
                                            <select
                                                className="form-control"
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            >
                                                <option value="">{question.placeholder}</option>
                                                {question.options.map(option => (
                                                    <option key={option.id} value={option.label}>{option.label}</option>
                                                ))}
                                            </select>
                                        )}
                                        {question.type === 'textarea' && (
                                            <textarea
                                                className="form-control"
                                                placeholder={question.placeholder}
                                                onChange={(e) => handleChange(question.fieldId, e.target.value)}
                                            />
                                        )}
                                    </div>
                                    {errors[question.fieldId] && (
                                        <small className="text-danger">{errors[question.fieldId]}</small>
                                    )}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
            <div className="text-center mt-4">
                <button className="btn btn-primary" onClick={handleSubmit}>Submit</button>
            </div>
        </div>
    );
};

export default OnePagePerSection;
